import React, { useState, useEffect } from 'react';

function MainPage({ onLogout }) {
  const [username, setUsername] = useState('');

  useEffect(() => {
    // Tutaj umieść logikę pobierania nazwy użytkownika z serwera
    fetch('/get-username') // Pobieramy nazwę użytkownika bez konieczności ponownego logowania
      .then(response => {
        if (!response.ok) {
          throw new Error('Błąd pobierania danych');
        }
        return response.json();
      })
      .then(data => {
        setUsername(data.username); // Ustawiamy nazwę użytkownika w stanie komponentu
      })
      .catch(error => console.error('Błąd pobierania nazwy użytkownika:', error));
  }, []); // Pusta tablica zależności oznacza, że efekt wykona się tylko raz po zamontowaniu komponentu

  return (
    <div>
      <h1>Witaj użytkowniku {username}</h1>
      <button onClick={onLogout}>Wyloguj się</button>
    </div>
  );
}

export default MainPage;
