import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Import the useNavigate hook

function MainPage({ onLogout }) {
  const [username, setUsername] = useState('');
  const navigate = useNavigate(); // Initialize the useNavigate hook

  useEffect(() => {
    // Place the logic for fetching the username from the server here
    fetch('/get-username')
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to fetch data');
        }
        return response.json();
      })
      .then(data => {
        setUsername(data.username);
      })
      .catch(error => console.error('Error fetching username:', error));
  }, []);

  // Function to handle logout
  const handleLogout = () => {
    // Logic for clearing session data or authentication tokens can be added here
    navigate('/'); // Redirect the user to the home page after logout
  };

  // Function to navigate to the cat images page
  const handleGoToCatsImages = () => {
    navigate('/CatsImages'); // Redirect the user to the cat images page
  };

  // Function to navigate to the cat facts page
  const handleGoToCatFacts = () => {
    navigate('/CatFacts'); // Redirect the user to the cat facts page
  };

  return (
    <div>
      <h1>Welcome, {username}</h1>
      <button onClick={handleLogout}>Logout</button> {/* Add logout button */}
      <button onClick={handleGoToCatsImages}>View Cat Images</button> {/* Add button to navigate to cat images page */}
      <button onClick={handleGoToCatFacts}>View Cat Facts</button> {/* Add button to navigate to cat facts page */}
    </div>
  );
}

export default MainPage;
