import React, { useState } from 'react';

function LoginPage() {
  const [loginData, setLoginData] = useState({
    username: '',
    password: ''
  });
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLoginData({ ...loginData, [name]: value });
  };

  const handleLogin = () => {
    if (!loginData.username || !loginData.password) {
      setError('Please fill in all fields.'); // Change the error message here
      return;
    }
    // Logic for login
  };

  return (
    <div>
      <h2>Login</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Error message display */}
      <div>
        <label>Username:</label>
        <input type="text" name="username" value={loginData.username} onChange={handleInputChange} />
      </div>
      <div>
        <label>Password:</label>
        <input type="password" name="password" value={loginData.password} onChange={handleInputChange} />
      </div>
      <button onClick={handleLogin}>Login</button>
    </div>
  );
}

export default LoginPage;
