import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom'; // Dodajemy useHistory do przekierowania użytkownika

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const history = useHistory(); // Inicjalizujemy history

  const handleLogin = () => {
    // Tutaj normalnie byłoby wykonanie żądania logowania, ale na potrzeby tego przykładu ustawiamy isLoggedIn na true
    setIsLoggedIn(true);
    // Przekierowanie użytkownika po zalogowaniu
    history.push('/MainPage'); // Przekierowanie na stronę główną po zalogowaniu
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Welcome to CatsApp</h1>
        {isLoggedIn ? (
          <div>
            <p>Pomyślnie zalogowano!</p>
            <button onClick={handleLogout}>Wyloguj się</button>
          </div>
        ) : (
          <div>
            <button onClick={handleLogin}>Login</button> {/* Zamieniamy Link na funkcję obsługi logowania */}
            <Link to="/register">
              <button>Register</button>
            </Link>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
