import React, { useState, useEffect } from 'react';

function MainPage({ onLogout }) {
  const [username, setUsername] = useState('');

  useEffect(() => {
    // Tutaj umieść logikę pobierania nazwy użytkownika z serwera
    fetchUsernameFromServer()
      .then(data => setUsername(data.username))
      .catch(error => console.error('Błąd pobierania nazwy użytkownika:', error));
  }, []); // Pusta tablica zależności oznacza, że efekt wykona się tylko raz po zamontowaniu komponentu

  // Funkcja do pobierania nazwy użytkownika z serwera
  const fetchUsernameFromServer = async () => {
    try {
      const response = await fetch('/get-username'); // Zakładając, że masz odpowiedni endpoint na serwerze do pobierania nazwy użytkownika
      if (!response.ok) {
        throw new Error('Błąd pobierania danych');
      }
      return await response.json();
    } catch (error) {
      throw error;
    }
  };

  return (
    <div>
      <h1>Witaj użytkowniku {username}</h1>
      <button onClick={onLogout}>Wyloguj się</button>
    </div>
  );
}

export default MainPage;
