import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import MainPage from './MainPage'; // Dodaj import komponentu MainPage

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState(''); // Dodaj stan dla nazwy użytkownika

  const handleLogout = () => {
    setUsername(''); // Wyczyść nazwę użytkownika po wylogowaniu
    setIsLoggedIn(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Welcome to CatsApp</h1>
        {isLoggedIn ? (
          <MainPage username={username} onLogout={handleLogout} /> // Przekazanie nazwy użytkownika i funkcji wylogowania do MainPage
        ) : (
          <div>
            <Link to="/login">
              <button>Login</button>
            </Link>
            <Link to="/register">
              <button>Register</button>
            </Link>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
