import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function ChatPage() {
  const [username, setUsername] = useState('');
  const [message, setMessage] = useState('');
  const [chatMessages, setChatMessages] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Pobierz nazwę użytkownika z local storage
    const storedUsername = localStorage.getItem('username');
    if (storedUsername) {
      setUsername(storedUsername);
    } else {
      // Jeśli nie ma nazwy użytkownika w local storage, przekieruj do strony logowania
      navigate('/login');
    }
  }, [navigate]);

  useEffect(() => {
    // Pobierz wiadomości czatu z serwera po załadowaniu komponentu
    fetchChatMessages();
  }, []);

  const fetchChatMessages = async () => {
    try {
      const response = await fetch('http://localhost:8081/get-chat-messages');
      if (!response.ok) {
        throw new Error('Error fetching chat messages');
      }
      const data = await response.json();
      setChatMessages(data.messages);
    } catch (error) {
      console.error('Error fetching chat messages:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:8081/send-message', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, message }),
      });

      if (response.ok) {
        // Zapisano wiadomość pomyślnie
        console.log('Message sent successfully!');
        // Pobierz najnowsze wiadomości czatu
        fetchChatMessages();
        // Wyczyść pole wiadomości
        setMessage('');
      } else {
        // Błąd podczas wysyłania wiadomości
        console.error('Error sending message');
      }
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  return (
    <div>
      <h2>Chat</h2>
      <div>
        {chatMessages.map((chatMessage, index) => (
          <div key={index}>
            <strong>{chatMessage.username}: </strong> {chatMessage.message}
          </div>
        ))}
      </div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={username}
          readOnly
          placeholder="Your username"
          required
        />
        <textarea
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
          required
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default ChatPage;
